import Head from 'next/head'
import Link from 'next/link'
import Layout from '../../components/layout.js'

import getCategories from "../../public/admin/functions/getCategories.js"

import { Grid, Typography, makeStyles } from "@material-ui/core"

const useStyles = makeStyles((theme) => ({
    heroImage: {
      background:"grey",
      height:"600px",
      width:"600px",
      borderRadius:"50%",
      backgroundColor:"#ffe5b4",
      margin:"0 auto"
    },
    image: {
      height:"250px",
      width:"250px",
      borderRadius:"50%",
      backgroundColor:"#ffe5b4"
    },
    mainGrid: {
      maxWidth:"775px",
      margin:"50px 0"
    },
    tileTitle: {
      margin:"15px auto 0",
      fontWeight:"bold"
    },
    tileSubTitle:{
      margin:"15px auto"
    },
    tileActionText: {
      borderBottom:"1px solid",
      width:"fit-content"
    },
    heroTitle: {
      fontWeight:"bold"
    },
    heroDescription: {
      margin:"15px 0"
    },
    homeLink: {
      color:"black",
      textDecoration:"none"
    },
    actionLink: {
      color:"black",
      textDecoration:"none"
    }
}))

export default (props) => {

  const classes = useStyles()
  const { data } = props

  const renderSteps = () => {

    return <Grid container className={ classes.stepsGrid }>
            {
              data.steps && subcategoryData.steps.map((x, y) => {
                return <Grid item xs={ 12 } sm={ 4 } key={ y }>
                          <Grid>
                            <Grid className={ classes.image }></Grid>
                          </Grid>
                          <Grid>
                            <Typography className={ classes.tileTitle }>{ x.title }</Typography>
                            <Typography className={ classes.tileSubTitle } style={{ width:"200px" }}>{ x.description }</Typography>
                          </Grid>
                       </Grid>
              })
            }
           </Grid>
  }

  return <Layout>
            <Grid>
              <Grid style={{ maxWidth:"900px" }}>
                <Typography variant="h2">{ data.pageTitle }</Typography>
                <Typography variant="h6">{ data.pageDescription }</Typography>
                <Grid container>
                  <Typography className={ classes.tileActionText } style={{ marginRight:"20px" }}>{ data.pageActionLink }</Typography>
                  <Link to="/" className={ classes.homeLink } style={{ marginRight:"20px" }}>
                    <Typography variant="body1" className={ classes.tileActionText }>HOME</Typography>
                  </Link>
                  <Link to={subcategoryData.category ? `/category/${data.category.id}` : "/"} className={ classes.homeLink }>
                    <Typography variant="body1" className={ classes.tileActionText }>BACK TO CATEGORY</Typography>
                  </Link>
                </Grid>
              </Grid>
              <Grid>{ this.renderSteps() }</Grid>
            </Grid>
          </Layout>
}

export async function getStaticPaths() {

  let paths = getCategories()
  paths = paths.map(x => {
    return {
      params: {
        id: x.name.split(" ").join("")
      }
    }
  })

  return  {
            paths,
            fallback:false
          }
}

export async function getStaticProps({ params }) {
  return {
    props: {
      data: getCategories().filter(x => x.name.split(" ").join("") == params.id)[0]
    }
  }
}
