let fs = require("fs")
let path = require("path")

export default () => {
  let filePath = "../admin/collections/How-tos"
  let folder = fs.readdirSync(filePath).map(name => join(filePath, name))
  let data = folder.map(x => JSON.parse(fs.readFileSync(x, 'utf-8')))
  return {
    data
  }
}
