import Head from 'next/head'
import Link from 'next/link'
import Layout from '../../components/layout.js'

import getCategories from "../../public/admin/functions/getCategories.js"

export default () => {
  return <Layout>
            <h1>Category</h1>
            <Link href="/"><a style={{ display:"block", margin:"10px 0" }}>Back Home</a></Link>
            <Link href="/subcategory"><a>To subcategory</a></Link>
          </Layout>
}

export async function getStaticPaths() {

  let paths = getCategories()
  paths = paths.map(x => {
    return {
      params: {
        id: x.name.split(" ").join("")
      }
    }
  })
  return  {
            paths,
            fallback:false
          }
}

export async function getStaticProps({ params }) {
  // Fetch necessary data for the blog post using params.id
  return {
    props : {
      name:"bryan"
    }
  }
}
