import Head from 'next/head'
import Link from 'next/link'
import Layout from '../../components/layout.js'

import getCategories from "../../public/admin/functions/getCategories.js"

import { Grid, Typography, makeStyles } from "@material-ui/core"

const useStyles = makeStyles((theme) => ({
    heroImage: {
      background:"grey",
      height:"600px",
      width:"600px",
      borderRadius:"50%",
      backgroundColor:"#ffe5b4",
      margin:"0 auto"
    },
    image: {
      height:"250px",
      width:"250px",
      borderRadius:"50%",
      backgroundColor:"#ffe5b4"
    },
    mainGrid: {
      maxWidth:"775px",
      margin:"50px 0"
    },
    tileTitle: {
      margin:"15px auto 0",
      fontWeight:"bold"
    },
    tileSubTitle:{
      margin:"15px auto"
    },
    tileActionText: {
      borderBottom:"1px solid",
      width:"fit-content"
    },
    heroTitle: {
      fontWeight:"bold"
    },
    heroDescription: {
      margin:"15px 0"
    },
    homeLink: {
      color:"black",
      textDecoration:"none"
    },
    actionLink: {
      color:"black",
      textDecoration:"none"
    }
}))

export default (props) => {

  const classes = useStyles()
  const { data } = props

